package com.higgs.trust.rs.core.vo;import com.alibaba.fastjson.JSONObject;import com.higgs.trust.rs.common.BaseBO;import com.higgs.trust.rs.common.enums.BizTypeEnum;import com.higgs.trust.slave.api.enums.VersionEnum;import com.higgs.trust.slave.model.bo.action.Action;import lombok.Getter;import lombok.Setter;import org.hibernate.validator.constraints.Length;import org.hibernate.validator.constraints.NotBlank;import javax.validation.Valid;import javax.validation.constraints.NotNull;import java.util.Date;import java.util.List;@Getter @Setter public class CoreTxVO extends BaseBO {    /**     * transaction id     */    @NotBlank    @Length(max = 64)    private String txId;    /**     * policy id     */    @NotBlank    @Length(max = 32)    private String policyId;    /**     * the list that store actions     */    @Valid    private List<Action> actionList;    /**     * the JSONObject that store data which will be packaged into blockchain .It  can be null , when there is no need for it     */    private JSONObject bizModel;    /**     * lock time     */    private Date lockTime;    /**     * the tx sender's rsId     */    @NotBlank    private String sender;    /**     * transaction version     */    @NotBlank    private String version;}