mapper.update:
UPDATE ca




			WHEN `user`=#{item.user}
			THEN now(3)

		ELSE `update_time`




			WHEN `user`=#{item.user}
			THEN #{item.period}

		ELSE `period`




			WHEN `user`=#{item.user}
			THEN #{item.pubKey}

		ELSE `pub_key`




			WHEN `user`=#{item.user}
			THEN #{item.valid}

		ELSE `valid`




		`user`=#{item.user}
