mapper.update:
UPDATE cluster_config

    update_time=now(3),

        node_num=#{nodeNum},


        fault_num=#{faultNum}


WHERE `cluster_name`=#{clusterName}

UPDATE cluster_config




            WHEN `cluster_name`=#{item.clusterName}
            THEN now(3)

        ELSE `update_time`




            WHEN `cluster_name`=#{item.clusterName}
            THEN #{item.nodeNum}

        ELSE `node_num`




            WHEN `cluster_name`=#{item.clusterName}
            THEN #{item.faultNum}

        ELSE `fault_num`




        `cluster_name`=#{item.clusterName}
