mapper.update:
UPDATE cluster_node

    update_time=now(3),

        p2p_status=#{p2pStatus},


        rs_status=#{rsStatus}


WHERE `node_name`=#{nodeName}

UPDATE cluster_node




            WHEN `node_name`=#{item.nodeName}
            THEN now(3)

        ELSE `update_time`




            WHEN `node_name`=#{item.nodeName}
            THEN #{item.p2pStatus}

        ELSE `p2p_status`




            WHEN `node_name`=#{item.nodeName}
            THEN #{item.rsStatus}

        ELSE `rs_status`




        `node_name`=#{item.nodeName}
